package io.zksync.helper;

import java.math.BigInteger;
import java.util.Arrays;
import java.util.Base64;
import java.util.Collections;

import io.zksync.transaction.manager.ZkSyncTransactionManager;
import org.web3j.abi.TypeReference;
import org.web3j.abi.datatypes.Function;
import org.web3j.abi.datatypes.Type;
import org.web3j.abi.datatypes.generated.Uint256;
import org.web3j.crypto.Credentials;
import org.web3j.ens.EnsResolver;
import org.web3j.protocol.Web3j;
import org.web3j.protocol.core.RemoteCall;
import org.web3j.protocol.core.methods.response.TransactionReceipt;
import org.web3j.tx.Contract;
import org.web3j.tx.TransactionManager;
import org.web3j.tx.gas.ContractGasProvider;
import org.web3j.tx.gas.DefaultGasProvider;
import org.web3j.utils.Numeric;

import io.zksync.crypto.signer.EthSigner;
import io.zksync.protocol.ZkSync;
import io.zksync.transaction.fee.ZkTransactionFeeProvider;

public class CounterContract extends Contract {

    public static final String FUNC_INCREMENT = "increment";
    public static final String FUNC_GET = "get";

    public static final String CODE = "";

    protected CounterContract(String contractBinary, String contractAddress, Web3j web3j, TransactionManager transactionManager, ContractGasProvider gasProvider) {
        super(contractBinary, contractAddress, web3j, transactionManager, gasProvider);
    }

    protected CounterContract(EnsResolver ensResolver, String contractBinary, String contractAddress, Web3j web3j, TransactionManager transactionManager, ContractGasProvider gasProvider) {
        super(ensResolver, contractBinary, contractAddress, web3j, transactionManager, gasProvider);
    }

    protected CounterContract(String contractBinary, String contractAddress, Web3j web3j, Credentials credentials, ContractGasProvider gasProvider) {
        super(contractBinary, contractAddress, web3j, credentials, gasProvider);
    }

    protected CounterContract(String contractAddress, Web3j web3j, TransactionManager transactionManager, ContractGasProvider gasProvider) {
        super("", contractAddress, web3j, transactionManager, gasProvider);
    }

    public static byte[] getCode() {
        return Base64.getDecoder().decode(CODE);
    }

    /**
     * Canonical contract load method of ZkSync2 SDK
     * 
     * @param contractAddress - Address of the deployed contract
     * @param zksync - ZkSync web3 client
     * @param feeProvider - Fee provider
     * @param signer - Ethereum message signer
     * @return remote call for deploy contract into blockchain
     */
    public static CounterContract load(String contractAddress, ZkSync zksync, ZkTransactionFeeProvider feeProvider, EthSigner signer) {
        return new CounterContract("", contractAddress, zksync, new ZkSyncTransactionManager(zksync, signer, feeProvider), new DefaultGasProvider());
    }

//    public static RemoteCall<CounterContract> deploy(ZkSync web3j, Credentials credentials, ZkTransactionFeeProvider feeProvider) {
//        return deployRemoteCall(CounterContract.class, web3j, credentials, feeProvider, Numeric.toHexString(getCode()), "");
//    }

    /**
     * Canonical contract deploy method of ZkSync2 SDK
     * 
     * @param zksync - ZkSync web3 client
     * @param feeProvider - Fee provider
     * @param signer - Ethereum message signer
     * @return remote call for deploy contract into blockchain
     */
    public static RemoteCall<CounterContract> deploy(ZkSync zksync, ZkTransactionFeeProvider feeProvider, EthSigner signer) {
        return deployRemoteCall(CounterContract.class, zksync, new ZkSyncTransactionManager(zksync, signer, feeProvider), new DefaultGasProvider(), Numeric.toHexString(getCode()), "");
    }

    public RemoteCall<TransactionReceipt> increment(BigInteger _value) {
        final Function function = encodeIncrement(_value);
        return executeRemoteCallTransaction(function);
    }

    public RemoteCall<BigInteger> get() {
        final Function function = new Function(FUNC_GET, 
                Arrays.<Type>asList(), 
                Arrays.<TypeReference<?>>asList(new TypeReference<Uint256>() {}));
        return executeRemoteCallSingleValueReturn(function, BigInteger.class);
    }

    public static Function encodeIncrement(BigInteger _value) {
        return new Function(
            FUNC_INCREMENT, 
            Arrays.<Type>asList(new org.web3j.abi.datatypes.generated.Uint256(_value)), 
            Collections.<TypeReference<?>>emptyList());
    }
    
}
