package io.zksync.transaction;

import static org.junit.jupiter.api.Assertions.*;

import java.math.BigInteger;
import java.util.Iterator;
import java.util.List;

import org.apache.commons.lang3.tuple.Pair;
import org.junit.Test;
import org.web3j.abi.FunctionEncoder;
import org.web3j.abi.datatypes.Address;
import org.web3j.abi.datatypes.Function;
import org.web3j.abi.datatypes.Type;
import org.web3j.abi.datatypes.generated.Uint256;
import org.web3j.utils.Convert;
import org.web3j.utils.Numeric;
import org.web3j.utils.Convert.Unit;

import io.zksync.abi.TransactionEncoder;
import io.zksync.crypto.eip712.Eip712Domain;
import io.zksync.crypto.eip712.Eip712Encoder;
import io.zksync.protocol.core.Token;
import io.zksync.protocol.core.ZkSyncNetwork;

public class TransferTest extends BaseTransactionTest {

    private static final Token TRANSFER_TOKEN = BaseTransactionTest.FEE_TOKEN;
    private static final String RECEIVER = "0xCcCCccccCCCCcCCCCCCcCcCccCcCCCcCcccccccC";

    @Test
    public void testSerializeToEIP712() {
        Transfer transfer = buildTransfer();
        List<Pair<String, Type<?>>> types = transfer.eip712types();
        Iterator<Pair<String, Type<?>>> t = types.iterator();

        {
            Pair<String, Type<?>> t2 = t.next();
            assertEquals("to", t2.getKey());
            assertEquals(new Address(RECEIVER), t2.getValue());
        }
        {
            Pair<String, Type<?>> t2 = t.next();
            assertEquals("token", t2.getKey());
            assertEquals(new Address(TRANSFER_TOKEN.getAddress()), t2.getValue());
        }
        {
            Pair<String, Type<?>> t2 = t.next();
            assertEquals("amount", t2.getKey());
            assertEquals(new Uint256(BigInteger.valueOf(1000000000000000000L)), t2.getValue());
        }
        super.assertSerializeToEIP712(t);
    }

    @Test
    public void testEncodeToEIP712TypeString() {
        Transfer transfer = buildTransfer();
        String result = Eip712Encoder.encodeType(transfer.intoEip712Struct());

        assertEquals(
                "Transfer(address to,address token,uint256 amount,address initiatorAddress,address feeToken,uint256 fee,uint32 nonce,uint64 validFrom,uint64 validUntil)",
                result);
    }

    @Test
    public void testSerializeToEIP712EncodedValue() {
        Transfer transfer = buildTransfer();
        byte[] encoded = Eip712Encoder.encodeValue(transfer.intoEip712Struct()).getValue();

        assertEquals("0x37cfda7c2b946d41fdfed6a60792d0dbe608df5cbf1082cf7edca527c64747c7",
                Numeric.toHexString(encoded));
    }

    @Test
    public void testSerializeToEIP712Message() {
        Transfer transfer = buildTransfer();
        byte[] encoded = Eip712Encoder.typedDataToSignedBytes(Eip712Domain.defaultDomain(ZkSyncNetwork.Localhost),
                transfer);

        assertEquals("0x110c25947cb3e5ec2f84afdb4864909c547250e57daac96112674b7ec203e59e",
                Numeric.toHexString(encoded));
    }

    @Test
    public void testSerializeToEncodedFunction() {
        Transfer transfer = buildTransfer();
        Function function = TransactionEncoder.encodeToFunction(transfer);

        assertEquals(
                "",
                FunctionEncoder.encode(function));
    }

    private Transfer buildTransfer() {
        Transfer zkTransfer = new Transfer(
                TRANSFER_TOKEN.getAddress(),
                RECEIVER,
                Convert.toWei("1", Unit.ETHER).toBigInteger(),
                SENDER.getAddress(),
                FEE_TOKEN.getAddress(),
                FEE,
                NONCE,
                VALIDITY_TIME);

        return zkTransfer;
    }
}
